package Sample_0328;

public class For_statement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1;i<=10; i++){
			System.out.println(i);
		}
		int j=10;
		while(j>=1){//for(int j =10; j>=1; j--)
			System.out.println(j*10);//j*10
			j--;
		}
		
	}

}